import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  deviceOption: {
    flex: 1,
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "center",
    borderRadius: 13,
    backgroundColor: "#1B1F3B",
  },
  icon: {
    flex: 0.2,
  },
  title: {
    flex: 0.6,
    fontFamily: "Roboto_500Medium",
    fontSize: 23,
    color: "white",
  },
});

export default styles;
